import './App.css';
import React, { Component } from 'react';
import GeneralInfo from './components/GeneralInfo';
import Education from './components/Education';
import Experience from './components/Experience';
import Title from './components/Title';
import Preview from './components/Preview';

class App extends Component {
  constructor() {
    super();
    this.state = {
      generalInfo: {
        firstName: '',
        lastName: '',
        mail: '',
        phone: ''
      },
      education: {
        school: '',
        city: '',
        subject: '',
        from: '',
        to: ''
      },
      experience: {
        position: '',
        company: '',
        city: '',
        from: '',
        to: ''
      },
      edit: true
    }

    this.handleChange = this.handleChange.bind(this);
    this.changeFirstName = this.changeFirstName.bind(this);
    this.changeLastName = this.changeLastName.bind(this);
    this.changeMail = this.changeMail.bind(this);
    this.changePhone = this.changePhone.bind(this);
    this.changeSchool =  this.changeSchool.bind(this);
    this.changeEducationCity = this.changeEducationCity.bind(this);
    this.changeSubject = this.changeSubject.bind(this);
    this.changeStartEducation = this.changeStartEducation.bind(this);
    this.changeEndEducation = this.changeEndEducation.bind(this);
    this.changePosition = this.changePosition.bind(this);
    this.changeCompany = this.changeCompany.bind(this);
    this.changeExperienceCity = this.changeExperienceCity.bind(this);
    this.changeStartExperience = this.changeStartExperience.bind(this);
    this.changeEndExperience = this.changeEndExperience.bind(this);
    this.changeEditMode = this.changeEditMode.bind(this);
  }

  changeFirstName(event) {
    const value = event.target.value;
    let prop = this.state.generalInfo;
    prop.firstName = value;
    this.setState({prop});
  }

  changeLastName(event) {
    const value = event.target.value;
    let prop = this.state.generalInfo;
    prop.lastName = value;
    this.setState({prop});
  }

  changeMail(event) {
    const value = event.target.value;
    let prop = this.state.generalInfo;
    prop.mail = value;
    this.setState({prop})
  }

  changePhone(event) {
    const value = event.target.value;
    let prop = this.state.generalInfo;
    prop.phone = value;
    this.setState({prop})
  }  

  changeSchool(event) {
    const value = event.target.value;
    let prop = this.state.education;
    prop.school = value;
    this.setState({prop})
  }

  changeEducationCity(event) {
    const value = event.target.value;
    let prop = this.state.education;
    prop.city = value;
    this.setState({prop})
  }

  changeSubject(event) {
    const value = event.target.value;
    let prop = this.state.education;
    prop.subject = value;
    this.setState({prop})
  }

  changeStartEducation(event) {
    const value = event.target.value;
    let prop = this.state.education;
    prop.from = value;
    this.setState({prop})
  }

  changeEndEducation(event) {
    const value = event.target.value;
    let prop = this.state.education;
    prop.to = value;
    this.setState({prop})
  }

  changePosition(event) {
    const value = event.target.value;
    let prop = this.state.experience;
    prop.position = value;
    this.setState({prop})
  }

  changeCompany(event) {
    const value = event.target.value;
    let prop = this.state.experience;
    prop.company = value;
    this.setState({prop})
  }

  changeExperienceCity(event) {
    const value = event.target.value;
    let prop = this.state.experience;
    prop.city = value;
    this.setState({prop})
  }

  changeStartExperience(event) {
    const value = event.target.value;
    let prop = this.state.experience;
    prop.from = value;
    this.setState({prop})
  }

  changeEndExperience(event) {
    const value = event.target.value;
    let prop = this.state.experience;
    prop.to = value;
    this.setState({prop})
  }

  handleChange(event) {

  }

  changeEditMode(event) {
    event.preventDefault();
    this.setState((prevState) => {
      if (prevState.edit === true) {
        return {
          edit: false
        }
      } else {
        return {
          edit: true
        }
      }
    })
  }

  render() {
    const edit = this.state.edit;
    if (edit) {
      return (
        <div>
          <Title />
          <form>
            <GeneralInfo changeFirstName={this.changeFirstName} changeLastName={this.changeLastName} changeMail={this.changeMail} changePhone={this.changePhone} edit={edit} state={this.state.generalInfo} group="general"/>
            <Education changeSchool={this.changeSchool} changeEducationCity={this.changeEducationCity} changeSubject={this.changeSubject} changeStartEducation={this.changeStartEducation}
            changeEndEducation={this.changeEndEducation} edit={edit} state={this.state.education} group="education"/>
            <Experience changePosition={this.changePosition} changeCompany={this.changeCompany} changeExperienceCity={this.changeExperienceCity} changeStartExperience={this.changeStartExperience} changeEndExperience={this.changeEndExperience} edit={edit} state={this.state.experience} group="experience"/>
            <button type="submit" onClick={this.changeEditMode}>Submit</button>
          </form>
        </div>
      ) 
    } else {
      return (
        <Preview state={this.state} handleClick={this.changeEditMode}/>
      )
    }

  }
}
export default App;
