import React, { Component } from 'react';

class Input extends Component {

    render() {
        const { name, onChange, edit, value } = this.props;
        console.log(value)
        if (edit) {
            return (
                <div>
                    <label>{name}</label>
                    <input type="text" onChange={onChange}></input>
                </div>
            )
        } else {
            return (
                <div>
                    <div>{name}</div>
                    <div>{value}</div>
                </div>
            )
        }

    }
}

export default Input;
