import './App.css';
import React, { Component } from 'react';
import GeneralInfo from './components/GeneralInfo';
import Education from './components/Education';
import Experience from './components/Experience';
import Title from './components/Title';
import Preview from './components/Preview';

class App extends Component {
  constructor() {
    super();
    this.state = {
      generalInfo: {
        firstName: '',
        lastName: '',
        mail: '',
        phone: ''
      },
      education: {
        school: '',
        city: '',
        subject: '',
        from: '',
        to: ''
      },
      experience: {
        position: '',
        company: '',
        city: '',
        from: '',
        to: ''
      },
      edit: true
    }

    this.handleChange = this.handleChange.bind(this);
    this.changeEditMode = this.changeEditMode.bind(this); 
  }

  handleChange(event) {
    const elem = event.target;
    let prop;
    let property;
    if (elem.classList.contains('general')) {
      prop = this.state.generalInfo;
      switch(elem.id) {
        case 'general-first-name':
          property = prop.firstName;
          break;
        case 'general-last-name':
          property = prop.lastName;
          break;
        case 'general-mail':
          property = prop.mail;
          break;
        case 'general-phone-number':
          property = prop.phone;
          break;
        default:
          property = property.firstName;
      }
    }

    if (elem.classList.contains('education')) {
      prop = this.state.education;
      switch(elem.id) {
        case 'education-school-name':
          property = prop.school;
          break;
        case 'education-city':
          property = prop.city;
          break;
        case 'education-subject':
          property = prop.subject;
          break;
        case 'education-from':
          property = prop.from;
          break;
        case 'education-to':
          property = prop.to;
          break;
        default:
          property = property.school;
      }
    }

    if (elem.classList.contains('experience')) {
      prop = this.state.experience;
      switch(elem.id) {
        case 'experience-position':
          property = prop.position;
          break;
        case 'experience-company':
          property = prop.company;
          break;
        case 'experience-city':
          property = prop.city;
          break;
        case 'experience-from':
          property = prop.from;
          break;
        case 'experience-to':
          property = prop.to;
          break;
        default:
          property = property.position;
      }
    }

  }

  changeEditMode(event) {
    event.preventDefault();
    this.setState((prevState) => {
      if (prevState.edit === true) {
        return {
          edit: false
        }
      } else {
        return {
          edit: true
        }
      }
    })
  }

  render() {
    const edit = this.state.edit;
    if (edit) {
      return (
        <div>
          <Title />
          <form>
            <GeneralInfo handleChange={this.handleChange} edit={edit} state={this.state.generalInfo} group="general"/>
            <Education handleChange={this.handleChange} edit={edit} state={this.state.education} group="education"/>
            <Experience handleChange={this.handleChange} edit={edit} state={this.state.experience} group="experience"/>
            <button type="submit" onClick={this.changeEditMode}>Submit</button>
          </form>
        </div>
      ) 
    } else {
      return (
        <Preview state={this.state} handleClick={this.changeEditMode}/>
      )
    }

  }
}
export default App;
