import React, { Component } from 'react';
import Input from './Input';

class Education extends Component {
    render() {
        const { changeSchool, changeEducationCity, changeSubject, changeStartEducation, changeEndEducation, edit } = this.props;
        return (
            <fieldset>
                <legend>Education</legend>
                <Input name="School Name" onChange={changeSchool} edit={edit}/>
                <Input name="City" onChange={changeEducationCity} edit={edit}/>
                <Input name="Subject" onChange={changeSubject} edit={edit}/>
                <Input name="From" onChange={changeStartEducation} edit={edit}/>
                <Input name="To" onChange={changeEndEducation} edit={edit}/>
            </fieldset>
        )
    }
}

export default Education;
