import React, { Component } from 'react';
import Input from './Input';

class GeneralInfo extends Component {
    render() {
        const { changeFirstName, changeLastName, changeMail, changePhone, edit, firstName, lastName, mail, phone } = this.props;
        return (
            <fieldset>
                <legend>General Info</legend>
                <Input name="First Name" onChange={changeFirstName} edit={edit} value={firstName}/>
                <Input name="Last Name" onChange={changeLastName} edit={edit}/>
                <Input name="Mail" onChange={changeMail} edit={edit}/>
                <Input name="Phone Number" onChange={changePhone} edit={edit}/>
            </fieldset>
        )
    }
}

export default GeneralInfo;
