import './App.css';
import React, { Component } from 'react';
import GeneralInfo from './components/GeneralInfo';
import Education from './components/Education';
import Experience from './components/Experience';
import Title from './components/Title';

class App extends Component {
  constructor() {
    super();
    this.state = {
      generalInfo: {
        firstName: '',
        lastName: '',
        mail: '',
        phone: ''
      },
      education: {
        school: '',
        city: '',
        subject: '',
        from: '',
        to: ''
      },
      experience: {
        position: '',
        company: '',
        city: '',
        from: '',
        to: ''
      }
    }

    this.changeFirstName = this.changeFirstName.bind(this);
    this.changeLastName = this.changeLastName.bind(this);
    this.changeMail = this.changeMail.bind(this);
    this.changePhone = this.changePhone.bind(this);
    this.changeSchool =  this.changeSchool.bind(this);
  }

  changeFirstName(event) {
    const value = event.target.value;
    this.setState({generalInfo: {
      firstName: value
    }});
  }

  changeLastName(event) {
    const value = event.target.value;
    this.setState({generalInfo: {
      lastName: value
    }}, () => console.log(this.state.generalInfo.lastName))
  }

  changeMail(event) {
    const value = event.target.value;
    this.setState({generalInfo: {
      mail: value
    }})
  }

  changePhone(event) {
    const value = event.target.value;
    this.setState({generalInfo: {
      phone: value
    }})
  }  

  changeSchool(event) {
    const value = event.target.value;
    this.setState({education: {
      school: value
    }})
  }



  render() {
    return (
      <div>
        <Title />
        <form>
          <GeneralInfo changeFirstName={this.changeFirstName} changeLastName={this.changeLastName} changeMail={this.changeMail} changePhone={this.changePhone}/>
          <Education changeSchool={this.changeSchool}/>
          <Experience />
          <button type="submit">Submit</button>
        </form>
      </div>
    ) 
  }
}
export default App;
