import React, { Component } from 'react';

class Input extends Component {

    render() {
        const { name, onChange } = this.props;
        return (
            <div>
                <label>{name}</label>
                <input type="text" onChange={onChange}></input>
            </div>
        )
    }
}

export default Input;
